    private void init() {
        ConfigPuerto config = new ConfigPuerto();
        Collection dispositivos = config.buscarDispositivos();
        for(Object dispositivo : dispositivos){
            this.cbDispositivo.addItem(dispositivo);
        }
    }
    
    public void actualizarVentana(String add){ //Muestra el paquete recibido
        this.taVentana.setText(add);
    }
